// SignInPage.js

import React, { useState } from 'react';
import './sign-in-page.css';

function SignInPage() {
  const [formData, setFormData] = useState({
    permanentAddress: '',
    residentialAddress: '',
    contactHome: '',
    contactPersonal1: '',
    contactPersonal2: '',
    email: '',
    district: '',
    fullName: '',
    nameWithInitials: '',
    policeDomain: '',
    gramasewakaArea: '',
    scannedNic: null,
    scannedBirthCertificate: null,
    recentPhotograph: null,
    nic: '',
    passport: '',
    drivingLicense: '',
    age: '',
    maritalStatus: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFormData({ ...formData, [name]: files[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add form submission logic here
    console.log(formData);
  };

  return (
    <div className="sign-in-page">
      <h2>Welcome to SLPD</h2>
      <h3>Create your profile</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form-column">
            <label htmlFor="permanentAddress">Permanent Address:</label>
            <input
              type="text"
              name="permanentAddress"
              value={formData.permanentAddress}
              onChange={handleChange}
            />
            {/* Add other left column fields */}
          </div>
          <div className="form-column">
            <label htmlFor="nic">NIC:</label>
            <input
              type="text"
              name="nic"
              value={formData.nic}
              onChange={handleChange}
            />
            <label htmlFor="passport">Passport (Optional):</label>
            <input
              type="text"
              name="passport"
              value={formData.passport}
              onChange={handleChange}
            />
            <label htmlFor="drivingLicense">Driving License (Optional):</label>
            <input
              type="text"
              name="drivingLicense"
              value={formData.drivingLicense}
              onChange={handleChange}
            />
            <label htmlFor="age">Age:</label>
            <input
              type="number"
              name="age"
              value={formData.age}
              onChange={handleChange}
            />
            <label htmlFor="maritalStatus">Marital Status:</label>
            <select
              name="maritalStatus"
              value={formData.maritalStatus}
              onChange={handleChange}
            >
              <option value="">Please select marital status</option>
              <option value="Married">Married</option>
              <option value="Unmarried">Unmarried</option>
            </select>
            {/* Add other right column fields */}
          </div>
        </div>
        <div className="form-row">
          <button type="submit">Create</button>
          <button type="button">Cancel</button>
        </div>
      </form>
    </div>
  );
}

export default SignInPage;
