import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import logo from '../assets/logo.png';
import './header.css';

function Header() {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const history = useHistory();

  const pages = [
    { 
      name: 'The Department', 
      path: '/department',
      keywords: ['police department', 'mission', 'history', 'executive team', 'inspector general']
    },
    { 
      name: 'Careers', 
      path: '/career',
      keywords: ['overview', 'career opportunities', 'eligibility', 'hiring']
    },
    // Add other pages with their associated keywords
  ];

  const getContent = (searchTerm) => {
    const normalizedSearchTerm = searchTerm.toLowerCase();

    // Search page names
    const matchedPage = pages.find(page =>
      page.name.toLowerCase().includes(normalizedSearchTerm)
    );

    if (matchedPage) {
      return matchedPage;
    }

    // Search page keywords
    const matchedPageByKeyword = pages.find(page =>
      page.keywords.some(keyword => keyword.includes(normalizedSearchTerm))
    );

    return matchedPageByKeyword;
  };

  const handleSearch = (searchTerm) => {
    setSearchTerm(searchTerm);

    if (searchTerm.trim() !== '') {
      const matchedPage = getContent(searchTerm);

      if (matchedPage) {
        setSuggestions([matchedPage]);
      } else {
        setSuggestions([]);
      }
    } else {
      setSuggestions([]);
    }
  };

  const clearSearch = () => {
    setSearchTerm('');
    setSuggestions([]);
  };

  const handleRoute = (path) => {
    history.push(path);
    clearSearch();
  };

  return (
    <header>
      <div className="logo-and-title-container">
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
        </div>
        <h1 className="title">EFFICIENCYXCEL SL</h1>
      </div>
      <div className="established">Established</div>
      <nav className="nav">
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/department">The Department</Link>
          </li>
          <li>
            <Link to="/career">Careers</Link>
          </li>
          <li>
            <Link to="/media-center">Media Center</Link>
          </li>
          <li>
            <Link to="/contact">Contact Us</Link>
          </li>
        </ul>
        <div className="search-bar">
          <input 
            type="text" 
            placeholder="Search..." 
            value={searchTerm} 
            onChange={(e) => handleSearch(e.target.value)} 
          />
          {searchTerm && (
            <button className="clear-button" onClick={clearSearch}>X</button>
          )}
          <button type="button" onClick={() => handleSearch(searchTerm)}>Search</button>
        </div>
      </nav>
      <div className="search-suggestions">
        {suggestions.length > 0 && (
          <ul>
            {suggestions.map(page => (
              <li key={page.path}>
                <Link to={page.path} onClick={() => handleRoute(page.path)}>{page.name}</Link>
              </li>
            ))}
          </ul>
        )}
      </div>
    </header>
  );
}

export default Header;
