import React, { useState } from 'react';

function App() {
  const [selectedOption, setSelectedOption] = useState('');
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    message: ''
  });
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleDropdownChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission here
    console.log('Form data submitted:', formData);
  };

  const handleAuth = () => {
    setIsLoggedIn(!isLoggedIn);
  };

  return (
    <div className="App">
      <div className="header">
        {isLoggedIn ? (
          <button onClick={handleAuth}>Sign Up</button>
        ) : (
          <button onClick={handleAuth}>Sign In</button>
        )}
      </div>
      <h1>Service Request Form</h1>
      <select value={selectedOption} onChange={handleDropdownChange}>
        <option value="">Select a service</option>
        <option value="Police Clearance">Police Clearance</option>
        <option value="Online Complaints">Online Complaints</option>
        <option value="Lost Mobiles">Lost Mobiles</option>
      </select>
      <form onSubmit={handleSubmit}>
        <label>
          Full Name:
          <input type="text" name="fullName" value={formData.fullName} onChange={handleInputChange} required />
        </label>
        <br />
        <label>
          Email:
          <input type="email" name="email" value={formData.email} onChange={handleInputChange} required />
        </label>
        <br />
        <label>
          Phone Number:
          <input type="tel" name="phoneNumber" value={formData.phoneNumber} onChange={handleInputChange} required />
        </label>
        <br />
        <label>
          Message:
          <textarea name="message" value={formData.message} onChange={handleInputChange} required />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
