import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import logo from '../assets/logo.png';
import './header.css';

function Header() {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const history = useHistory();

  const pages = [
    { name: 'Home', path: '/' },
    { name: 'The Department', path: '/department' },
    { name: 'Careers', path: '/career' },
    { name: 'Media Center', path: '/media-center' },
    { name: 'Contact Us', path: '/contact' }
  ];

  // This is a placeholder function to get content based on the page path
  const getContent = (path) => {
    // Implement logic to fetch content based on path
    // For example, if you have page content stored in an object or database
    // you can retrieve it using the path
    switch (path) {
      case '/department':
        return 'History of the Department';
      case '/career':
        return 'Career Opportunities';
      case '/media-center':
        return 'Media Center Overview';
      case '/contact':
        return 'Contact Information';
      default:
        return ''; // Return empty string if no content found
    }
  };

  const handleSearch = (searchTerm) => {
    // Update search term
    setSearchTerm(searchTerm);

    if (searchTerm.trim() !== '') {
      // Check if search term matches specific content identifiers
      const matchedPages = pages.filter(page => {
        // Check if the page content contains the search term
        const content = getContent(page.path); // You need to define this function to get content based on page path
        return content.toLowerCase().includes(searchTerm.toLowerCase());
      });

      // Set suggestions based on matched pages
      setSuggestions(matchedPages);
    } else {
      // Clear suggestions when search term is empty
      setSuggestions([]);
    }
  };

  const clearSearch = () => {
    // Clear search term and suggestions
    setSearchTerm('');
    setSuggestions([]);
  };

  const handleRoute = (path) => {
    history.push(path);
    clearSearch();
  };

  return (
    <header>
      <div className="logo-and-title-container">
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
        </div>
        <h1 className="title">EFFICIENCYXCEL SL</h1>
      </div>
      <div className="established">Established</div>
      <nav className="nav">
        <ul>
          {pages.map(page => (
            <li key={page.path}>
              <Link to={page.path}>{page.name}</Link>
            </li>
          ))}
        </ul>
        <div className="search-bar">
          <input 
            type="text" 
            placeholder="Search..." 
            value={searchTerm} 
            onChange={(e) => handleSearch(e.target.value)} 
          />
          {searchTerm && (
            <button className="clear-button" onClick={clearSearch}>X</button>
          )}
          <button type="button" onClick={() => handleSearch(searchTerm)}>Search</button>
        </div>
      </nav>
      {/* Display search suggestions */}
      <div className="search-suggestions">
        {suggestions.length > 0 && (
          <ul>
            {suggestions.map(page => (
              <li key={page.path}>
                <Link to={page.path} onClick={() => handleRoute(page.path)}>{page.name}</Link>
              </li>
            ))}
          </ul>
        )}
      </div>
    </header>
  );
}

export default Header;
