import React, { useState } from 'react';
import './signin.css';
import logo from '../assets/logo.png';
import axios from 'axios';
import Footer from '../component/footer';

function SignInPage() {
  const [formData, setFormData] = useState({
    permanentAddress: '',
    residentialAddress: '',
    contactHome: '',
    contactPersonal1: '',
    contactPersonal2: '',
    email: '',
    district: '',
    division: '', // New state to store the selected division
    fullName: '',
    nameWithInitials: '',
    nic: '',
    passport: '',
    drivingLicense: '',
    age: '',
    maritalStatus: '',
    policeDomain: '',
    gramasewakaArea: '',
    scannedNic: null,
    scannedBirthCertificate: null,
    recentPhotograph: null,
    password: '' 
  });

  // Sample division data structure, replace it with actual data
  const divisionsByDistrict = {
    "Colombo": ["Division 1", "Division 2", "Division 3"],
    "Gampaha": ["Division 4", "Division 5", "Division 6"],
    // Add divisions for other districts
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // When district changes, reset the division selection
    if (name === 'district') {
      setFormData({ ...formData, division: '' });
    }
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setFormData({ ...formData, [name]: files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/auth/register', formData);
      console.log('Form submitted successfully:', response.data);
      // Add any additional logic after successful submission
    } catch (error) {
      console.error('Error submitting form:', error);
      // Handle errors here
    }
  };

  return (
    <div className="sign-in-page">
      <div className="header">
        <img src={logo} alt="Logo" className="logo" />
        <h2>Welcome to SLPD</h2>
      </div>
      <h3>Create your profile</h3>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <div className="form-row1">
            <div className="form-column">
              {/* Other input fields */}
              <label htmlFor="district">District:</label>
              <select
                name="district"
                value={formData.district}
                onChange={handleChange}
              >
                <option value="">Please select district</option>
                {Object.keys(divisionsByDistrict).map((district, index) => (
                  <option key={index} value={district}>{district}</option>
                ))}
              </select>
              {/* Render divisions based on the selected district */}
              <label htmlFor="division">Division:</label>
              <select
                name="division"
                value={formData.division}
                onChange={handleChange}
                disabled={!formData.district} // Disable if no district selected
              >
                <option value="">Please select division</option>
                {formData.district && divisionsByDistrict[formData.district].map((division, index) => (
                  <option key={index} value={division}>{division}</option>
                ))}
              </select>
            </div>
            {/* Other form columns */}
          </div>
          <div className="form-row">
            <div className="button-container">
              <button type="submit">Create</button>
              <button type="button">Cancel</button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}

export default SignInPage;
