// Header.js

import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import logo from '../assets/logo.png';
import './header.css';

function Header() {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const history = useHistory();

  // Function to handle search input change
  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    // Filter suggestions based on input
    const filteredSuggestions = pages.filter(page =>
      page.toLowerCase().includes(value.toLowerCase())
    );
    setSuggestions(filteredSuggestions);
  };

  // Function to handle suggestion click
  const handleSuggestionClick = (suggestion) => {
    setSearchTerm('');
    setSuggestions([]);
    history.push(`/${suggestion.toLowerCase().replace(/\s+/g, '-')}`);
  };

  // List of available pages
  const pages = [
    "Home",
    "The Department",
    "Our Mission",
    "Our History",
    "Executive Team",
    "Office Of Inspector General",
    "Careers",
    "Overview",
    "Career Opportunities",
    "Eligibility & Hiring",
    "Media Center",
    "Contact Us"
  ];

  return (
    <header>
      <div className="logo-and-title-container">
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
        </div>
        <h1 className="title">EFFICIENCYXCEL SL</h1>
      </div>
      <div className="established">Established</div>
      <nav className="nav">
        <ul>
          {pages.map((page, index) => (
            <li key={index}>
              <Link to={`/${page.toLowerCase().replace(/\s+/g, '-')}`}>{page}</Link>
            </li>
          ))}
        </ul>
        <div className="search-bar">
          <input 
            type="text" 
            placeholder="Search..."
            value={searchTerm}
            onChange={handleInputChange}
          />
          {suggestions.length > 0 && (
            <ul className="suggestions">
              {suggestions.map((suggestion, index) => (
                <li key={index} onClick={() => handleSuggestionClick(suggestion)}>
                  {suggestion}
                </li>
              ))}
            </ul>
          )}
          <button type="submit">Search</button>
        </div>
      </nav>
    </header>
  );
}

export default Header;
